public class literals {

    public static void main(String[] args) {
        // Integer literals
        int decimal = 100;
        int octal = 0144; // 100 in octal
        int hexadecimal = 0x64; // 100 in hexadecimal

        // Floating-point literals
        float floatLiteral = 10.5f;
        double doubleLiteral = 20.5;

        // Character literals
        char charLiteral = 'A';

        // String literals
        String stringLiteral = "Hello, World!";
        String Mob_ = "7258993306";

        // Boolean literals
        boolean trueLiteral = true;
        boolean falseLiteral = false;

        // Print all literals
        System.out.println("Integer Literals:");
        System.out.println("Decimal: " + decimal);
        System.out.println("Octal: " + octal);
        System.out.println("Hexadecimal: " + hexadecimal);
        System.out.println("\nFloating-point Literals:");
        System.out.println("Float: " + floatLiteral);
        System.out.println("Double: " + doubleLiteral);
        System.out.println("\nCharacter Literal:");
        System.out.println("Char: " + charLiteral);
        System.out.println("\nString Literal:");
        System.out.println("String: " + stringLiteral);
        System.out.println("Mobile no: " + Mob_);
        System.out.println("\nBoolean Literals:");
        System.out.println("True: " + trueLiteral);
        System.out.println("False: " + falseLiteral);
    } 
}
